public X min<X>(X x, X... xs) 
        given X satisfies Comparable<X> {
    mutable X min := x;
    for (X y in xs) { 
        if (y<min) { 
            min:=y;
        }
    }
    return min
}

public X max<X>(X x, X... xs) 
        given X satisfies Comparable<X> {
    mutable X max := x;
    for (X y in xs) { 
        if (y>max) { 
            max:=y;
        }
    }
    return max
}

