        satisfies Iterable<X>, Container {
    override Iterator<X> iterator() {
        class SequenceIterator(Natural from) 
                satisfies Iterator<X> {
            override X? head { 
                return sequence[from] 
            }
            override Iterable<X> tail {
                return SequenceIterator(from+1)
            }
